package myproject;
import java.util.Scanner;

public class Shift_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a=10;
		 Scanner sc=new Scanner (System.in);
		 int b=sc.nextInt();
		System.out.println(a<<b);
		
		System.out.println(a>>b);
        
	}

}
